    val listDestination: ArrayList<Destination>
        get() {
            var list = arrayListOf<Destination>()
            for (position in destinationNames.indices){
                var destination = Destination(
                    name = destinationNames[position],
                    location = destinationLocation[position],
                    description = destinationDescription[position],
                    open = destinationOpen[position],
                    ticket = destinationTicket[position],
                    photo1 = destinationPhoto1[position],
                    photo2 = destinationPhoto2[position],
                    photo3 = destinationPhoto3[position]
                )
                list.add(destination)
            }
            return list
        }
}